package com.example.hw_3_6

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import com.example.hw_3_6.databinding.FragmentPhoneBinding

class PhoneFragment : Fragment() {

    private lateinit var binding: FragmentPhoneBinding
    private var phoneItemList = arrayListOf(
        PhoneModel("Pixel 5", "https://m-cdn.phonearena.com/images/phones/82853-350/Google-Pixel-5a.jpg?w=1"),
        PhoneModel("Pixel 7", "data:image/jpeg;base64,"),
        PhoneModel("Nothing phone (1) 5", "data:image/jpeg;base64,"),
        PhoneModel("Asus Zenphone 9 5", "https://m.media-amazon.com/images/W/MEDIAX_792452-T2/images/I/71u-XdCNsuL._AC_UF894,1000_QL80_.jpg"),
        PhoneModel("Nokia Lumia 630", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSuCnyz2s3AG2UtFTVoFDqUXEodtOuqUAW4MQ&usqp=CAU"),
        PhoneModel("Iphone 12", "data:image/jpeg;base64,")
    )

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentPhoneBinding.inflate(layoutInflater)
        return binding.root
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        val adapter = PhoneAdapter(phoneItemList)
        binding.rvPhone.adapter = adapter
    }
}